}

.context h1 {
    color: #fff;
    font-size: 50px;
}

/* Animated Background */
.area {
    width: 100%;
    height: 100vh;
    background: linear-gradient(to left, #8f94fb, #4e54c8);
}

/* Floating Circles */
.circles {
    position: absolute;
    width: 100%;
    height: 100%;
    overflow: hidden;
}

.circles li {
    position: absolute;
    display: block;
    list-style: none;
    background: rgba(255, 255, 255, 0.2);
    border-radius: 50%;
    animation: animate 25s linear infinite;
    bottom: -150px;
}

/* Circle Properties */
.circles li:nth-child(1) {
    left: 25%;
    width: 80px;
    height: 80px;
}

.circles li:nth-child(2) {
    left: 10%;
    width: 20px;
    height: 20px;
    animation-delay: 2s;
    animation-duration: 12s;
}

.circles li:nth-child(3) {
    left: 70%;
    width: 20px;
    height: 20px;
    animation-delay: 4s;
}

.circles li:nth-child(4) {
    left: 40%;
    width: 60px;
    height: 60px;
    animation-duration: 18s;
}

.circles li:nth-child(5) {
    left: 65%;
    width: 20px;
    height: 20px;
}

.circles li:nth-child(6) {
    left: 75%;
    width: 110px;
    height: 110px;
    animation-delay: 3s;
}

.circles li:nth-child(7) {
    left: 35%;
    width: 150px;
    height: 150px;
    animation-delay: 7s;
}

.circles li:nth-child(8) {
    left: 50%;
    width: 25px;
    height: 25px;
