var numSelected = null;
var tileSelected = null;
var errors = 0;

const board = [
    "--74916-5", "2---6-3-9", "-----7-1-",
    "-586----4", "--3----9-", "--62--187",
    "9-4-7---2", "67-83----", "81--45---"
];

const solution = [
    "387491625", "241568379", "569327418",
    "758619234", "123784596", "496253187",
    "934176852", "675832941", "812945763"
];

window.onload = function () {
    setGame();
};

function setGame() {
    // Create number selection
    for (let i = 1; i <= 9; i++) {
        let number = document.createElement("div");
        number.id = i;
        number.innerText = i;
        number.classList.add("number");
        number.addEventListener("click", selectNumber);
        document.getElementById("digits").appendChild(number);
    }

    // Generate board
    for (let r = 0; r < 9; r++) {
        for (let c = 0; c < 9; c++) {
            let tile = document.createElement("div");
            tile.id = `${r}-${c}`;
            tile.classList.add("tile");

            if (board[r][c] !== "-") {
                tile.innerText = board[r][c];
                tile.classList.add("tile-start");
            }

            // Add thicker grid lines
            if (r % 3 === 2) tile.classList.add("horizontal-line");
            if (c % 3 === 2) tile.classList.add("vertical-line");

            tile.addEventListener("click", selectTile);
            document.getElementById("board").appendChild(tile);
        }
    }
}

function selectNumber() {
    if (numSelected) numSelected.classList.remove("number-selected");
    numSelected = this;
    numSelected.classList.add("number-selected");
}

function selectTile() {
    if (!numSelected) return;
    if (this.innerText !== "") return; // Prevent overwriting numbers

    let [r, c] = this.id.split("-").map(Number);

    if (solution[r][c] === numSelected.id) {
        this.innerText = numSelected.id;
        this.style.color = "green"; // Correct answer effect
    } else {
        errors++;
        document.getElementById("errors").innerText = errors;
        this.style.color = "red"; // Wrong answer effect
    }
}

