    currentExpression += value;
    resultInput.value = currentExpression;
}

function calculate() {
    try {
        const result = eval(currentExpression);
        resultInput.value = result;
        currentExpression = "";
    } catch (error) {
        resultInput.value = "Error";
    }
}

function clearResult() {
    currentExpression = "";
    resultInput.value = "";
}

