/* HTML: <div id='tooltip-1'>This is a Tooltip with a solid coloration and without border radius</div> */
#tooltip-1 {
  color: #fff;
  font-size: 18px;
  max-width: 28ch;
  text-align: center;
}
#tooltip-1 {
  /* triangle dimension */
  --b: 2em; /* base */
  --h: 1em; /* height */

  --p: 50%; /* triangle position (0%:left 100%:right) */

  padding: 1em;
  clip-path: polygon(0 100%,0 0,100% 0,100% 100%,
    min(100%,var(--p) + var(--b)/2) 100%,
    var(--p) calc(100% + var(--h)),
    max(0%  ,var(--p) - var(--b)/2) 100%);
  border-image: fill 0//var(--h)
    conic-gradient(#CC333F 0 0); /* the color */
}

